'use strict';

var expect = require('chai').expect;
var sinon = require('sinon');
var proxyquire = require('proxyquire');
var fs = require('fs');
var path = require('path');
require('chai').use(require('sinon-chai'));
require('sinon-as-promised');

var soap = require('../../../lib/helpers/soap');

describe('Player', function () {
  var zoneMemberData = void 0;
  var request = void 0;
  var Player = void 0;
  var player = void 0;
  var Subscriber = void 0;
  var subscriber = void 0;
  var listener = void 0;
  var system = void 0;
  var musicServices = void 0;

  var TYPE = require('../../../lib/helpers/soap').TYPE;

  beforeEach(function () {
    sinon.stub(soap, 'invoke').resolves();
    sinon.stub(soap, 'parse');
  });

  afterEach(function () {
    if (soap.invoke.restore) soap.invoke.restore();
    if (soap.parse.restore) soap.parse.restore();
  });

  beforeEach(function () {
    zoneMemberData = {
      uuid: 'RINCON_00000000000001400',
      location: 'http://192.168.1.151:1400/xml/device_description.xml',
      zonename: 'Kitchen',
      icon: 'x-rincon-roomicon:kitchen',
      configuration: '1',
      softwareversion: '31.8-24090',
      mincompatibleversion: '29.0-00000',
      legacycompatibleversion: '24.0-00000',
      bootseq: '114',
      wirelessmode: '0',
      hasconfiguredssid: '0',
      channelfreq: '2412',
      behindwifiextender: '0',
      wifienabled: '1',
      orientation: '0',
      sonarstate: '4'
    };

    request = sinon.spy();

    subscriber = {
      dispose: sinon.spy()
    };

    Subscriber = sinon.stub().returns(subscriber);

    musicServices = {
      tryGetHighResArt: sinon.stub()
    };

    musicServices.tryGetHighResArt.onCall(0).resolves('http://example.org/image1');
    musicServices.tryGetHighResArt.onCall(1).resolves('http://example.org/image2');

    Player = proxyquire('../../../lib/models/Player', {
      '../helpers/request': request,
      '../Subscriber': Subscriber,
      '../musicservices': musicServices
    });

    listener = {
      endpoint: sinon.stub().returns('http://127.0.0.2/'),
      on: sinon.spy()
    };

    system = {
      zones: [{
        uuid: zoneMemberData.uuid,
        members: []
      }],
      on: sinon.stub(),
      emit: sinon.spy()
    };

    player = new Player(zoneMemberData, listener, system);
    player.coordinator = player;
    system.zones[0].coordinator = player;
    system.zones[0].members.push(player);
  });

  it('Should set name on player', function () {
    expect(player.roomName).equal('Kitchen');
  });

  it('Should have uuid', function () {
    expect(player.uuid).equal('RINCON_00000000000001400');
  });

  it('Has baseUrl', function () {
    expect(player.baseUrl).equal('http://192.168.1.151:1400');
  });

  it('Subscribes to the various notifications by default', function () {
    expect(Subscriber).callCount(4);
    expect(Subscriber.withArgs('http://192.168.1.151:1400/MediaRenderer/AVTransport/Event', 'http://127.0.0.2/')).calledOnce;
    expect(Subscriber.withArgs('http://192.168.1.151:1400/MediaRenderer/RenderingControl/Event', 'http://127.0.0.2/')).calledOnce;
    expect(Subscriber.withArgs('http://192.168.1.151:1400/MediaRenderer/GroupRenderingControl/Event', 'http://127.0.0.2/')).calledOnce;
    expect(Subscriber.withArgs('http://192.168.1.151:1400/MediaServer/ContentDirectory/Event', 'http://127.0.0.2/')).calledOnce;
  });

  it('Invokes dispose on all listeners when disposing player', function () {
    player.dispose();
    expect(subscriber.dispose).callCount(4);
  });

  it('Subscribes to listener events', function () {
    expect(listener.on).calledTwice;
  });

  describe('When it receives a transport-state update for queue playback', function () {
    beforeEach(function (done) {
      var lastChange = require('../../data/avtransportlastchange.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      player.on('transport-state', function () {
        done();
      });
    });

    it('Updates state', function () {
      expect(player.state.playbackState).equals('PLAYING');
      expect(player.state.trackNo).equals(43);
      expect(player.state.currentTrack).eql({
        artist: 'Johannes Brahms',
        title: 'Intermezzo No. 3 in C-sharp minor, Op. 117 - Andante con moto',
        album: 'Glenn Gould plays Brahms: 4 Ballades op. 10; 2 Rhapsodies op. 79; 10 Intermezzi',
        albumArtUri: '/getaa?s=1&u=x-sonos-spotify%3aspotify%253atrack%253a5qAFqkXoQd2RfjZ2j1ay0w%3fsid%3d9%26flags%3d8224%26sn%3d9',
        absoluteAlbumArtUri: 'http://example.org/image1',
        duration: 318,
        uri: 'x-sonos-spotify:spotify%3atrack%3a5qAFqkXoQd2RfjZ2j1ay0w?sid=9&flags=8224&sn=9',
        type: 'track'
      });
      expect(player.state.nextTrack).eql({
        artist: 'Coheed and Cambria',
        title: 'Here To Mars',
        album: 'The Color Before The Sun',
        albumArtUri: '/getaa?s=1&u=x-sonos-spotify%3aspotify%253atrack%253a0Ap3aOVU7LItcHIFiRF8lY%3fsid%3d9%26flags%3d8224%26sn%3d9',
        absoluteAlbumArtUri: 'http://example.org/image2',
        duration: 241,
        uri: 'x-sonos-spotify:spotify%3atrack%3a0Ap3aOVU7LItcHIFiRF8lY?sid=9&flags=8224&sn=9'
      });

      expect(player.state.playMode).eql({
        repeat: 'all',
        shuffle: true,
        crossfade: true
      });
    });

    it('Updates avTransportUri', function () {
      expect(player.avTransportUri).equals('x-rincon-queue:RINCON_00000000000001400#0');
    });

    it('Updates avTransportUriMetadata', function () {
      expect(player.avTransportUriMetadata).equals('');
    });
  });

  describe('When it recieves a transport-state update for radio playback', function () {
    beforeEach(function (done) {
      var lastChange = require('../../data/avtransportlastchange_radio.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      player.on('transport-state', function () {
        done();
      });
    });

    it('Updates state', function () {
      expect(player.state.playbackState).equals('PLAYING');
      expect(player.state.trackNo).equals(1);
      expect(player.state.currentTrack).eql({
        artist: 'Lugna Favoriter',
        title: 'Leona Lewis - Bleeding Love',
        album: undefined,
        albumArtUri: '/getaa?s=1&u=x-sonosapi-stream%3as17553%3fsid%3d254%26flags%3d8224%26sn%3d0',
        absoluteAlbumArtUri: 'http://192.168.1.151:1400/getaa?s=1&u=x-sonosapi-stream%3as17553%3fsid%3d254%26flags%3d8224%26sn%3d0',
        duration: 0,
        uri: 'x-sonosapi-stream:s17553?sid=254&flags=8224&sn=0',
        type: 'radio'
      });

      expect(player.state.nextTrack).eql({
        artist: '',
        title: '',
        album: '',
        albumArtUri: '',
        duration: 0,
        uri: ''
      });
    });

    it('Updates avTransportUri', function () {
      expect(player.avTransportUri).equals('x-sonosapi-stream:s17553?sid=254&amp;flags=8224&amp;sn=0');
    });

    it('Updates avTransportUriMetadata', function () {
      expect(player.avTransportUriMetadata).equals('<DIDL-Lite xmlns:dc="http://purl.org/dc/elements/1.1/"xmlns:upnp="urn:schemas-upnp-org:metadata-1-0/upnp/" xmlns:r="urn:schemas-rinconnetworks-com:metadata-1-0/" xmlns="urn:schemas-upnp-org:metadata-1-0/DIDL-Lite/"><item id="-1" parentID="-1" restricted="true"><dc:title>Lugna Favoriter</dc:title><upnp:class>object.item.audioItem.audioBroadcast</upnp:class><desc id="cdudn" nameSpace="urn:schemas-rinconnetworks-com:metadata-1-0/">SA_RINCON65031_</desc></item></DIDL-Lite>');
    });
  });

  describe('When radio already has an absolute url', function () {
    beforeEach(function (done) {
      soap.invoke.resolves();
      var lastChange = require('../../data/avtransportlastchange_radio.json');
      lastChange.currenttrackmetadata.val = '<DIDL-Lite xmlns:dc="http://purl.org/dc/elements/1.1/" xmlns:upnp="urn:schemas-upnp-org:metadata-1-0/upnp/" xmlns:r="urn:schemas-rinconnetworks-com:metadata-1-0/" xmlns="urn:schemas-upnp-org:metadata-1-0/DIDL-Lite/"><item id="-1" parentID="-1" restricted="true"><res protocolInfo="sonos.com-http:*:application/octet-stream:*">x-sonosapi-stream:s20308?sid=254&amp;flags=32</res><r:streamContent>P5 STHLM - Sebastian Ingrosso - Dark River</r:streamContent><r:radioShowMd></r:radioShowMd><upnp:albumArtURI>http://absolute.url/for/test</upnp:albumArtURI><dc:title>x-sonosapi-stream:s20308?sid=254&amp;flags=32</dc:title><upnp:class>object.item</upnp:class></item></DIDL-Lite>';
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      player.on('transport-state', function () {
        done();
      });
    });

    it('Should not touch absoluteAlbumUri', function () {
      expect(player.state.currentTrack.absoluteAlbumArtUri).equal('http://absolute.url/for/test');
    });
  });

  describe('when volume event occurs', function () {
    it('Updates volume', function () {
      var lastChange = require('../../data/renderingControlLastChange.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);

      expect(player.state.volume).equals(12);
      expect(player.groupState.volume).equals(12);
    });

    it('outputFixed is false', function () {
      var lastChange = require('../../data/renderingControlLastChange.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      expect(player.outputFixed).equals(false);
    });

    it('outputFixed is true', function () {
      var lastChange = require('../../data/renderingControlLastChange.json');
      lastChange.outputfixed.val = '1';
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      expect(player.outputFixed).equals(true);
    });

    it('emits event', function () {
      var lastChange = require('../../data/renderingControlLastChange.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      expect(system.emit).calledOnce;
    });
  });

  it('Loads prototypes', function () {
    expect(player).respondsTo('replaceWithFavorite');
  });

  describe('commands', function () {

    var simpleCases = [{ type: TYPE.Play, action: 'play' }, { type: TYPE.Pause, action: 'pause' }, { type: TYPE.Next, action: 'nextTrack' }, { type: TYPE.Previous, action: 'previousTrack' }];
    simpleCases.forEach(function (test) {
      it('' + test.action, function () {
        expect(test.type, test.action).not.undefined;
        return player[test.action]().then(function () {
          expect(soap.invoke.firstCall.args, test.action).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', test.type]);
        });
      });
    });

    beforeEach(function () {
      player._state.volume = 5;
    });

    var volumeCases = [{ type: TYPE.Volume, action: 'setVolume', value: 10, expectation: 10 }, { type: TYPE.Volume, action: 'setVolume', value: '+1', expectation: 6 }, { type: TYPE.Volume, action: 'setVolume', value: '-1', expectation: 4 }];
    volumeCases.forEach(function (test) {
      it('Volume ' + test.value + ' should be ' + test.expectation, function () {
        expect(test.type, test.action).not.undefined;
        return player[test.action](test.value).then(function () {
          expect(soap.invoke.firstCall.args, test.action).eql(['http://192.168.1.151:1400/MediaRenderer/RenderingControl/Control', test.type, { volume: test.expectation }]);
        });
      });
    });

    it('Volume with fixedoutput should short circuit', function () {
      player.outputFixed = true;
      return player.setVolume(10).then(function () {
        expect(soap.invoke).not.called;
      });
    });

    var muteCases = [{ type: TYPE.Mute, action: 'mute', expectation: 1 }, { type: TYPE.Mute, action: 'unMute', expectation: 0 }];
    muteCases.forEach(function (test) {
      it('' + test.action, function () {
        expect(test.type, test.action).not.undefined;
        return player[test.action]().then(function () {
          expect(soap.invoke.firstCall.args, test.action).eql(['http://192.168.1.151:1400/MediaRenderer/RenderingControl/Control', test.type, { mute: test.expectation }]);
        });
      });
    });

    var seekCases = [{ type: TYPE.Seek, action: 'timeSeek', value: 120, expectation: { unit: 'REL_TIME', value: '00:02:00' } }, { type: TYPE.Seek, action: 'trackSeek', value: 12, expectation: { unit: 'TRACK_NR', value: 12 } }];
    seekCases.forEach(function (test) {
      it('' + test.action, function () {
        expect(test.type, test.action).not.undefined;
        return player[test.action](test.value).then(function () {
          expect(soap.invoke.firstCall.args, test.action).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', test.type, test.expectation]);
        });
      });
    });

    it('clearQueue', function () {
      expect(TYPE.RemoveAllTracksFromQueue).not.undefined;
      return player.clearQueue().then(function () {
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.RemoveAllTracksFromQueue]);
      });
    });

    it('removeTrackFromQueue', function () {
      expect(TYPE.RemoveTrackFromQueue).not.undefined;
      return player.removeTrackFromQueue(13).then(function () {
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.RemoveTrackFromQueue, { track: 13 }]);
      });
    });

    describe('Playmode dependant tests', function () {

      it('Repeat with no state', function () {
        expect(TYPE.SetPlayMode).not.undefined;
        return player.repeat(true).then(function () {
          expect(soap.invoke).calledOnce;
          expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetPlayMode, { playMode: 'REPEAT_ALL' }]);
        });
      });

      it('Repeat with shuffle on', function () {
        player.state.playMode.shuffle = true;
        return player.repeat(true).then(function () {
          expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetPlayMode, { playMode: 'SHUFFLE' }]);
        });
      });

      it('Shuffle on with no other state', function () {
        expect(TYPE.SetPlayMode).not.undefined;
        return player.shuffle(true).then(function () {
          expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetPlayMode, { playMode: 'SHUFFLE_NOREPEAT' }]);
        });
      });

      it('Shuffle off', function () {
        player.state.playMode.repeat = 'all';
        return player.shuffle(true).then(function () {
          expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetPlayMode, { playMode: 'SHUFFLE' }]);
        });
      });

      it('Crossfade on', function () {
        expect(TYPE.SetCrossfadeMode).not.undefined;
        return player.crossfade(true).then(function () {
          expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetCrossfadeMode, { crossfadeMode: 1 }]);
        });
      });

      it('Crossfade off', function () {
        return player.crossfade(false).then(function () {
          expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetCrossfadeMode, { crossfadeMode: 0 }]);
        });
      });

      describe('when playmode fails', function () {

        beforeEach(function () {
          soap.invoke.onCall(0).rejects();
        });

        it('Still calls crossfade if playmode fails', function () {
          return player.setPlayMode({ repeat: false, crossfade: true }).then(function () {
            expect(soap.invoke).calledTwice;
          });
        });
      });
    });

    it('Sleep', function () {
      expect(TYPE.ConfigureSleepTimer).not.undefined;
      return player.sleep(120).then(function () {
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.ConfigureSleepTimer, { time: '00:02:00' }]);
      });
    });

    it('setAVTransport', function () {
      expect(TYPE.SetAVTransportURI).not.undefined;
      return player.setAVTransport('x-rincon:RINCON_00000000000001400', '<DIDL-Lite></DIDL-Lite>').then(function () {
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetAVTransportURI, {
          uri: 'x-rincon:RINCON_00000000000001400',
          metadata: '&lt;DIDL-Lite&gt;&lt;/DIDL-Lite&gt;'
        }]);
      });
    });

    it('setAVTransport without metadata', function () {
      expect(TYPE.SetAVTransportURI).not.undefined;
      return player.setAVTransport('x-rincon:RINCON_00000000000001400').then(function () {
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.SetAVTransportURI, {
          uri: 'x-rincon:RINCON_00000000000001400',
          metadata: ''
        }]);
      });
    });

    it('becomeCoordinatorOfStandaloneGroup', function () {
      expect(TYPE.BecomeCoordinatorOfStandaloneGroup).not.undefined;
      return player.becomeCoordinatorOfStandaloneGroup().then(function () {
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.BecomeCoordinatorOfStandaloneGroup]);
      });
    });

    it('refreshShareIndex', function () {
      expect(TYPE.RefreshShareIndex).not.undefined;
      return player.refreshShareIndex().then(function () {
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaServer/ContentDirectory/Control', TYPE.RefreshShareIndex]);
      });
    });

    it('addURIToQueue', function () {
      soap.parse.restore();
      var addURIToQueueXml = fs.createReadStream(__dirname + '/../../data/addURIToQueue.xml');
      addURIToQueueXml.statusCode = 200;
      soap.invoke.resolves(addURIToQueueXml);

      expect(TYPE.AddURIToQueue).not.undefined;
      return player.addURIToQueue('x-rincon:RINCON_00000000000001400', '<DIDL-Lite></DIDL-Lite>').then(function (result) {
        expect(result).eql({
          firsttracknumberenqueued: '1',
          newqueuelength: '1',
          numtracksadded: '1'
        });
        expect(soap.invoke).calledOnce;
        expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.AddURIToQueue, {
          uri: 'x-rincon:RINCON_00000000000001400',
          metadata: '&lt;DIDL-Lite&gt;&lt;/DIDL-Lite&gt;',
          enqueueAsNext: 0,
          desiredFirstTrackNumberEnqueued: 0
        }]);
      });
    });
  });

  describe('Position of track progress should be fetched', function () {
    beforeEach(function (done) {
      player.on('transport-state', function () {
        done();
      });
      var lastChange = require('../../data/avtransportlastchange.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
    });

    it('GetPositionInfo is requested', function () {

      expect(TYPE.GetPositionInfo).not.undefined;
      expect(soap.invoke).calledOnce;
      expect(soap.invoke.firstCall.args).eql(['http://192.168.1.151:1400/MediaRenderer/AVTransport/Control', TYPE.GetPositionInfo]);
    });
  });

  describe('Using fake timers', function () {
    var clock = void 0;
    var now = void 0;

    beforeEach('We need parse functionality here', function () {
      soap.parse.restore();
    });

    beforeEach(function (done) {
      var positionXml = fs.createReadStream(__dirname + '/../../data/getpositioninfo.xml');
      positionXml.statusCode = 200;
      soap.invoke.resolves(positionXml);
      var lastChange = require('../../data/avtransportlastchange.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      player.on('transport-state', function () {
        done();
      });
    });

    beforeEach(function () {
      now = Date.now();
      clock = sinon.useFakeTimers(now);
    });

    afterEach(function () {
      clock.restore();
    });

    it('GetPositionInfo is saved', function () {
      expect(player.state.elapsedTime).equal(142);
    });

    it('elapsedTime is dynamically calculated', function () {
      var positionXml = fs.createReadStream(__dirname + '/../../data/getpositioninfo.xml');
      positionXml.statusCode = 200;
      soap.invoke.resolves(positionXml);
      var lastChange = require('../../data/avtransportlastchange.json');
      listener.on.withArgs('last-change').yield('RINCON_00000000000001400', lastChange);
      clock.tick(6000);
      expect(player.state.elapsedTime).equal(148);
    });
  });

  describe('Browse-inherited functions', function () {

    beforeEach('We need parse functionality here', function () {
      soap.parse.restore();
    });

    describe('Return queue', function () {
      var queue = void 0;
      beforeEach(function () {
        var queueStream = fs.createReadStream(path.join(__dirname, '../../data/queue.xml'));

        soap.invoke.resolves(queueStream);

        return player.getQueue().then(function (q) {
          queue = q;
        });
      });

      it('Should have invoked browse', function () {
        expect(soap.invoke).calledOnce;
        expect(soap.invoke.firstCall.args[2]).eql({
          objectId: 'Q:0',
          startIndex: 0,
          limit: 0
        });
      });

      it('Parses queue and returns a list of well designed objects', function () {
        expect(queue).not.empty;
        expect(queue[0]).eql({
          uri: 'x-sonos-spotify:spotify%3atrack%3a2uAWmcvujYUNTPCIb2VYKH?sid=9&flags=8224&sn=2',
          artist: 'Deftones',
          metadata: undefined,
          title: 'Prayers/Triangles',
          album: 'Prayers/Triangles',
          albumArtUri: '/getaa?s=1&u=x-sonos-spotify%3aspotify%253atrack%253a2uAWmcvujYUNTPCIb2VYKH%3fsid%3d9%26flags%3d8224%26sn%3d2'
        });
      });
    });

    describe('Parsing playlists', function () {
      var queue = void 0;
      beforeEach(function () {
        var queueStream = fs.createReadStream(path.join(__dirname, '../../data/playlists.xml'));

        soap.invoke.resolves(queueStream);

        return player.browse().then(function (q) {
          queue = q;
        });
      });

      it('Parses response and returns a list of well designed objects', function () {
        expect(queue.items).not.empty;
        expect(queue.startIndex).equal(0);
        expect(queue.numberReturned).equal(2);
        expect(queue.totalMatches).equal(2);
        expect(queue.items[0]).eql({
          uri: 'file:///jffs/settings/savedqueues.rsq#2',
          title: 'Morgon',
          albumArtUri: ['/getaa?s=1&u=x-sonos-spotify%3aspotify%253atrack%253a35N1AduT1LDo3deLfYniTY%3fsid%3d9%26flags%3d0', '/getaa?s=1&u=x-sonos-spotify%3aspotify%253atrack%253a1MQYow43CGLYMECVSjTpCM%3fsid%3d9%26flags%3d0', '/getaa?s=1&u=x-sonos-spotify%3aspotify%253atrack%253a4QWMYALvB1m4Um8ytjZR9m%3fsid%3d9%26flags%3d0', '/getaa?s=1&u=x-sonos-spotify%3aspotify%253atrack%253a1d62ECx2DlaBmhOLymrVGc%3fsid%3d9%26flags%3d0']
        });
      });
    });
  });
});