'use strict';

var expect = require('chai').expect;
var sinon = require('sinon');
require('chai').use(require('sinon-chai'));

describe('SonosSystem.applyPreset', function () {
  var applyPreset = require('../../../../lib/prototypes/SonosSystem/applyPreset.js');

  describe('When applying a preset', function () {
    var system = void 0;
    var player = void 0;
    var preset = void 0;
    var superfluousPlayer = void 0;
    var otherPlayer = void 0;

    before(function () {
      player = {
        roomName: 'Kitchen',
        play: sinon.stub().resolves(),
        setVolume: sinon.stub().resolves(),
        pause: sinon.stub().resolves(),
        setAVTransport: sinon.stub().resolves(),
        replaceWithFavorite: sinon.stub().resolves(),
        setPlayMode: sinon.stub().resolves(),
        trackSeek: sinon.stub().resolves(),
        timeSeek: sinon.stub().resolves(),
        sleep: sinon.stub().resolves(),
        becomeCoordinatorOfStandaloneGroup: sinon.stub().resolves(),
        uuid: 'RINCON_0000000001400'
      };

      otherPlayer = {
        roomName: 'Other room',
        pause: sinon.stub().resolves(),
        uuid: 'RINCON_1000000001400'
      };

      player.coordinator = player;

      superfluousPlayer = {
        becomeCoordinatorOfStandaloneGroup: sinon.stub().resolves()
      };

      system = {
        getPlayer: sinon.stub().returns(player),
        zones: [{
          uuid: 'RINCON_0000000001400',
          coordinator: player,
          members: [player, player, player, superfluousPlayer]
        }, { coordinator: otherPlayer }]
      };

      preset = {
        test: {
          players: [{ roomName: 'Bedroom', volume: 1 }, { roomName: 'Kitchen', volume: 2 }, {
            roomName: 'Office',
            volume: 3
          }],
          playMode: {
            crossfade: true,
            repeat: true,
            shuffle: true
          },
          pauseOthers: true,
          favorite: 'My favorite',
          trackNo: 12,
          elapsedTime: 120,
          state: 'playing',
          sleep: 600
        }
      };

      return applyPreset.call(system, preset.test);
    });

    it('Pauses all zones', function () {
      expect(otherPlayer.pause).calledOnce;
    });

    it('Has invoked getPlayer thrice', function () {
      expect(system.getPlayer.firstCall.args[0]).equal(preset.test.players[0].roomName);
      expect(system.getPlayer.secondCall.args[0]).equal(preset.test.players[1].roomName);
      expect(system.getPlayer.thirdCall.args[0]).equal(preset.test.players[2].roomName);
    });

    it('Has set volume correctly', function () {
      expect(player.setVolume).calletThrice;
      expect(player.setVolume.firstCall.args[0]).equal(1);
      expect(player.setVolume.secondCall.args[0]).equal(2);
      expect(player.setVolume.thirdCall.args[0]).equal(3);
    });

    it('Should not break out coordinator since already coordinator', function () {
      expect(player.becomeCoordinatorOfStandaloneGroup).not.called;
    });

    it('Groups players with coordinator', function () {
      expect(player.setAVTransport).calledTwice;
      expect(player.setAVTransport.firstCall.args[0]).equal('x-rincon:RINCON_0000000001400');
      expect(player.setAVTransport.secondCall.args[0]).equal('x-rincon:RINCON_0000000001400');
    });

    it('Ungroups players that does\'nt belong to group', function () {
      expect(superfluousPlayer.becomeCoordinatorOfStandaloneGroup).calledOnce;
    });

    it('Replaces queue with favorite', function () {
      expect(player.replaceWithFavorite).calledOnce;
      expect(player.replaceWithFavorite.firstCall.args[0]).equal(preset.test.favorite);
    });

    it('Sets correct playmode', function () {
      expect(player.setPlayMode).calledOnce;
      expect(player.setPlayMode.firstCall.args[0]).eql(preset.test.playMode);
    });

    it('Skips to correct track', function () {
      expect(player.trackSeek).calledOnce;
      expect(player.trackSeek.firstCall.args[0]).equal(preset.test.trackNo);
    });

    it('Skips to correct time', function () {
      expect(player.timeSeek).calledOnce;
      expect(player.timeSeek.firstCall.args[0]).equal(preset.test.elapsedTime);
    });

    it('Should call sleep', function () {
      expect(player.sleep).calledOnce;
      expect(player.sleep.firstCall.args[0]).equal(preset.test.sleep);
    });

    it('Should start playback', function () {
      expect(player.play).calledOnce;
    });
  });

  describe('When coordinator is not coordinator of any group', function () {
    var system = void 0;
    var player = void 0;
    var preset = void 0;
    var coordinator = void 0;

    before(function () {
      player = {
        setVolume: sinon.stub().resolves(),
        setAVTransport: sinon.stub().resolves(),
        uuid: 'RINCON_0000000001400'
      };

      coordinator = {
        play: sinon.stub().resolves(),
        setVolume: sinon.stub().resolves(),
        becomeCoordinatorOfStandaloneGroup: sinon.stub().resolves(),
        uuid: 'RINCON_10000000001400',
        coordinator: player
      };

      system = {
        getPlayer: sinon.stub().returns(player),
        zones: [{ uuid: 'RINCON_0000000001400', coordinator: coordinator }, { coordinator: player }]
      };

      system.getPlayer.onCall(0).returns(coordinator);

      preset = {
        test: {
          players: [{ roomName: 'Bedroom', volume: 1 }, { roomName: 'Kitchen', volume: 2 }, {
            roomName: 'Office',
            volume: 3
          }]
        }
      };

      return applyPreset.call(system, preset.test);
    });

    it('Detaches first player from group', function () {
      expect(coordinator.becomeCoordinatorOfStandaloneGroup).calledOnce;
    });
  });

  describe('When preset contains uri only', function () {
    var system = void 0;
    var player = void 0;
    var preset = void 0;
    var coordinator = void 0;

    before(function () {
      player = {
        play: sinon.stub().resolves(),
        setAVTransport: sinon.stub().resolves(),
        becomeCoordinatorOfStandaloneGroup: sinon.stub().resolves(),
        uuid: 'RINCON_0000000001400'
      };

      system = {
        getPlayer: sinon.stub().returns(player),
        zones: [{ uuid: 'RINCON_0000000001400', coordinator: player }]
      };

      preset = {
        test: {
          players: [{ roomName: 'Bedroom' }],
          uri: 'x-rincon-stream:UUID_0000000001400',
          metadata: '<DIDL-Lite></DIDL-Lite>'
        }
      };

      return applyPreset.call(system, preset.test);
    });

    it('Sets uri to preset uri and metadata', function () {
      expect(player.setAVTransport).calledOnce;
      expect(preset.test.uri).not.undefined;
      expect(preset.test.metadata).not.undefined;
      expect(player.setAVTransport.firstCall.args[0]).equal(preset.test.uri);
      expect(player.setAVTransport.firstCall.args[1]).equal(preset.test.metadata);
    });
  });
});