'use strict';

var expect = require('chai').expect;
var sinon = require('sinon');
var proxyquire = require('proxyquire');
require('chai').use(require('sinon-chai'));

describe('getFavorites', function () {

  var player = void 0;
  var system = void 0;
  var resultMock = {
    items: [{
      uri: 'test://uri',
      title: 'title'
    }, {
      uri: undefined,
      title: undefined
    }], startIndex: 0
  };
  var success = void 0;

  var getFavorites = require('../../../../lib/prototypes/SonosSystem/getFavorites.js');

  describe('When calling getFavorites', function () {
    before(function () {
      player = {
        browseAll: sinon.stub().resolves(resultMock.items)
      };

      system = {
        getAnyPlayer: sinon.stub().returns(player)
      };

      success = sinon.spy();

      return getFavorites.call(system).then(success);
    });

    it('Has called browseAll', function () {
      expect(player.browseAll).calledOnce;
      expect(player.browseAll.firstCall.args).eql(['FV:2']);
    });

    it('Returns the expected result with undefined values filtered out', function () {
      expect(success.firstCall.args[0]).eql(resultMock.items);
    });
  });
});