'use strict';

var expect = require('chai').expect;

describe('deezer', function () {

  var deezer = void 0;

  beforeEach(function () {
    var apiStub = require('../../../lib/services/deezer');
    var servicesContainer = {};

    apiStub(servicesContainer);

    deezer = servicesContainer[2];
  });

  it('should load highres art', function () {
    return deezer.tryGetHighResArt('x-sonosprog-http:tr-flac%3a3134041.flac?sid=2&flags=8224&sn=7').then(function (url) {
      expect(url).to.contain('deezer.com/images/cover/5646492f1aec0168e52814d27d2d9a67/500x500-000000-80-0-0.jpg');
    });
  });
});