'use strict';

var expect = require('chai').expect;

describe('soundcloud', function () {

  var soundcloud = void 0;

  beforeEach(function () {
    var apiStub = require('../../../lib/services/soundcloud');
    var servicesContainer = {};

    apiStub(servicesContainer);

    soundcloud = servicesContainer[160];
  });

  it('should load highres art', function () {
    return soundcloud.tryGetHighResArt('x-sonos-http:track%3a44731098.mp3?sid=160&flags=8224&sn=10').then(function (url) {
      expect(url).to.equal('https://i1.sndcdn.com/artworks-000022486019-txiq8s-t500x500.jpg');
    });
  });
});