'use strict';

var expect = require('chai').expect;

describe('spotify', function () {

  var spotify = void 0;

  beforeEach(function () {
    var apiStub = require('../../../lib/services/spotify');
    var servicesContainer = {};

    apiStub(servicesContainer);

    spotify = servicesContainer[9];
  });

  it('should load highres art', function () {

    return spotify.tryGetHighResArt('x-sonos-spotify:spotify%3atrack%3a3WKg25vrbjJlkhsgl2W4p3?sid=9&flags=8224&sn=9').then(function (url) {
      expect(url).to.have.string('https://i.scdn.co/image/');
    });
  });
});